-- Yu-Gi-Oh Set Data
-- Auto-generated from card_sets.json
-- Total sets: 1004 (2 sets without tcg_date excluded)

CREATE TABLE IF NOT EXISTS sets (
  id INT AUTO_INCREMENT PRIMARY KEY,
  set_name VARCHAR(255) NOT NULL,
  set_code VARCHAR(50) NOT NULL,
  num_of_cards INT NOT NULL,
  tcg_date DATE NOT NULL,
  set_image VARCHAR(500),
  is_a_session BOOLEAN NOT NULL DEFAULT FALSE,
  is_purchasable BOOLEAN NOT NULL DEFAULT TRUE,
  is_promo BOOLEAN NOT NULL DEFAULT FALSE,
  KEY idx_set_code (set_code),
  KEY idx_tcg_date (tcg_date)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;












